(951, 1, '2020-11-07 18:08:03', '2020-11-07 18:08:03', '<h2>Linha do Tempo</h2>\r\n<div class=\"timeline\">\r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1894</h2>\r\n      <p>Fundação do Grupo Espírita Allan Kardec.</p>\r\n    </div>\r\n  </div>\r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1898</h2>\r\n      <p>Grupo Espírita Allan Kardec denominou-se Sociedade Espírita Allan Kardec Criação da Revista Espírita.</p>\r\n    </div>\r\n  </div>\r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1899</h2>\r\n      <p>Bons Frutos: Fundação do Grupo Espírita Aurora em Santa Rita do Rio dos Sinos.</p>\r\n    </div>\r\n  </div>\r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1901</h2>\r\n      <p>Bons Frutos: Fundação do Grupo Espírita Fé e Esperança na cidade de Dom Pedrito.</p>\r\n    </div>\r\n  </div>\r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1905</h2>\r\n      <p>Bons Frutos: Fundação dos Grupos Espíritas São Vicente de Paula e São Marcos (POA) e do Grupo Espírita Luz Beneficente (Rio Grande). Registro da primeira Biblioteca da SEAK.</p>\r\n    </div>\r\n  </div>\r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1911</h2>\r\n      <p>Aquisição do prédio da Rua Riachuelo, no 54, pela SEAK.</p>\r\n    </div>\r\n  </div>\r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1912</h2>\r\n      <p>Bons Frutos: Fundação da Sociedade Humanitária Jesus de Nazareno (Hospital Espírita de Porto Alegre).</p>\r\n    </div>\r\n  </div>\r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1914</h2>\r\n      <p>Aquisição do prédio no 146 da Rua Gal. Vitorino. Montagem do Laboratório e da Farmácia da SEAK.</p>\r\n    </div>\r\n  </div>\r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1921</h2>\r\n      <p>Bons Frutos: Fundação da FERGS. Fundação da Soc. de Investigações Psíquicas (Soc. Esp. Maria Nazaré).</p>\r\n    </div>\r\n  </div>\r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1921</h2>\r\n      <p>Frederico Gomes Augusto da Silva, presidente da SEAK e um dos idealistas da FERGS, organiza o 1o Congresso Espírita do RS, na SEAK.</p>\r\n    </div>\r\n  </div>    \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1922</h2>\r\n      <p>Bons Frutos: Fundação da Sociedade Espírita Paz e Amor. Criação do jornal</p>\r\n    </div>\r\n  </div>    \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1925</h2>\r\n      <p>Bons Frutos: Fundação da Sociedade Legião Espírita.</p>\r\n    </div>\r\n  </div>    \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1933</h2>\r\n      <p>Ingresso da SEAK no quadro federativo da Federação Espírita do Rio Grande do Sul (FERGS).</p>\r\n    </div>\r\n  </div>    \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1941</h2>\r\n      <p>Criação da Caixa de Assistência aos Necessitados Carlos Pareta e do Dispensário Homeopático Xavier Carneiro.</p>\r\n    </div>\r\n  </div>    \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1942</h2>\r\n      <p>Criação do Roupeiro Infantil Mercedes Ferrari.</p>\r\n    </div>\r\n  </div>    \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1946</h2>\r\n      <p>Aquisição do prédio da Rua Andrade Neves, no 60.</p>\r\n    </div>\r\n  </div>   \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1949</h2>\r\n      <p>Inauguração do prédio da Rua Andrade Neves, no 60.</p>\r\n    </div>\r\n  </div>   \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1951</h2>\r\n      <p>2o Congresso Espírita do Rio Grande do Sul organizado pela SEAK.</p>\r\n    </div>\r\n  </div>   \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1952</h2>\r\n      <p>Criação do Jornal A Luz de Damasco.</p>\r\n    </div>\r\n  </div>   \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1975</h2>\r\n      <p>Inauguração da Biblioteca Enapino Brusque Borges de Andrade.</p>\r\n    </div>\r\n  </div>   \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1979</h2>\r\n      <p>Bons Frutos: Fundação da Sociedade Espírita Vinha de Luz, em Cachoeirinha.</p>\r\n    </div>\r\n  </div>   \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1980</h2>\r\n      <p>Aquisição do prédio da Rua Fernando Machado, no 883.</p>\r\n    </div>\r\n  </div>   \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>1986</h2>\r\n      <p>Inauguração do prédio da Rua Fernando Machado, no 883.</p>\r\n    </div>\r\n  </div>   \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>2013</h2>\r\n      <p>Mudança da sede da Rua Andrade Neves para o prédio da Rua Fernando Machado.</p>\r\n    </div>\r\n  </div>   \r\n  <div class=\"container-timeline timeline-right\">\r\n    <div class=\"content-timeline\">\r\n      <h2>2016</h2>\r\n      <p>As atividades do prédio da Rua Andrade Neves são transferidas para o prédio da Rua Fernando Machado.</p>\r\n    </div>\r\n  </div>   \r\n \r\n  \r\n  \r\n  \r\n  \r\n  \r\n  \r\n  \r\n    \r\n</div>\r\n\r\n\r\n\r\n', 'Linha do Tempo', '', 'publish', 'closed', 'closed', '', 'linha-do-tempo', '', '', '2020-11-29 19:20:55', '2020-11-29 19:20:55', '', 958, 'http://localhost/seak1/?page_id=951', 0, 'page', '', 0),
